import React from 'react';

const Pagination = (props) => {
    const pageLinks = []

    for(let i = 1; i <= props.pages + 1; i++){
        let active = props.currentPage == i ? 'active' : '';
        pageLinks.push(<li className="PageLink" key={i} onClick={() => props.nextPages(i)}><a href="#">{i}</a></li>)
    }

    return (
        <div className="container">
            <div className="row">
                <ul className="pagination">
                    { props.currentPage > 1 ? <li className="PageLink" onClick={() => props.nextPages(props.currentPage - 1)}><a href="#">Prev</a></li>: "" }
                    { pageLinks }
                    { props.currentPage < props.pages + 1 ? <li className="PageLink" onClick={() => props.nextPages(props.currentPage - 1)}><a href="#">Next</a></li>: "" }
                </ul>
            </div>
        </div>
    )
}

export default Pagination;
