import React, { Component } from 'react';
import SearchArea from './SearchArea';
import request from 'superagent';
import BookList from './BookList';

class Books extends Component {

    //Create state
    constructor(props){
        super(props);
        this.state = {
            books: [],
            searchField: '',
            sort: ''
        }
    }

    //Fetch data from API
    searchBooks = (e) => {
        e.preventDefault();
        request
            .get("https://www.googleapis.com/books/v1/volumes")
            .query({ q: this.state.searchField })
            .then((data) => {
                this.setState({ books: [...data.body.items] });
            })
    }


    //Create search handler
    handleSearch = (e) => {
        this.setState({ searchField: e.target.value })
    }

    //Sort out handler
    handleSearch = (e) => {
        this.setState({ sort: e.target.value })
    }

    //Clean up data
    cleanData = (e) => {
        const cleanData = data.body.item.map((book) => {
            if(book.volumeInfo.hasOwnProperty('publishedData') === false){
                book.volumeInfo['publishedDate'] = '0000';
            }
            else if(book.volumeInfo.hasOwnProperty('imagelinks') === false){
                book.volumeInfo['imageLinks'] = { thumbnail: 'https://www.google.com/url?sa=i&source=images&cd=&ved=2ahUKEwjWrJ2DnfHlAhXa7XMBHW2GAskQjRx6BAgBEAQ&url=http%3A%2F%2Fwww.bigmusicgeek.com%2Fgeekcasts%2F&psig=AOvVaw1FZaPEF0aZmwVbKrz0I_2J&ust=1574079436628201'}
            }
        return Books;
        })
    return cleanData;   
    }


    //Pass "handleSearch" to <SearchArea> component
	render() {  
		return (
		    <div >
                <SearchArea searchBooks={this.searchBooks} handleSearch={this.handleSearch} handleSort={this.handleSort}/>
                <BookList books={this.state.books} />
		    </div>
		  );
		}
	}

export default Books;
